package com.meizu.simplify.utils;import java.io.*;public class ByteUtil implements Serializable {	private static final long serialVersionUID = -605369732281294495L;	public static byte[] ObjectToByte(Object obj) {        byte[] bytes = new byte[1024];        try {            ByteArrayOutputStream bo = new ByteArrayOutputStream();            ObjectOutputStream oo = new ObjectOutputStream(bo);            oo.writeObject(obj);            bytes = bo.toByteArray();            bo.close();            oo.close();        } catch (Exception e) {            e.printStackTrace();        }        return (bytes);    }    public static Object ByteToObject(byte[] bytes) {        Object obj = new Object();        try {            ByteArrayInputStream bi = new ByteArrayInputStream(bytes);            ObjectInputStream oi = new ObjectInputStream(bi);            obj = oi.readObject();            bi.close();            oi.close();        } catch (Exception e) {            e.printStackTrace();        }        return obj;    }}